const rstbtEle=document.querySelector(".restart-button"),elStyle=rstbtEle.style;rstbtEle.onmousemove=function(t){const e=rstbtEle.getBoundingClientRect(),l=t.clientX-e.left,r=t.clientY-e.top,o=l-e.width/2,n=r-e.height/2;elStyle.setProperty("--bt_rx",n/-5+"deg"),elStyle.setProperty("--bt_ry",o/10+"deg")},rstbtEle.onmouseleave=function(t){elStyle.setProperty("--bt_ty","0"),elStyle.setProperty("--bt_rx","0"),elStyle.setProperty("--bt_ry","0")},rstbtEle.onmousedown=function(t){elStyle.setProperty("--bt_tz","-5rem")},rstbtEle.onmouseup=function(t){elStyle.setProperty("--bt_tz","-12px")};